mapper.insert:

INSERT INTO tb_cm_menu
(menu_id, menu_nm, menu_pw, frst_reg_dt, frst_reg_id, last_proc_dt, last_proc_id
, menu_yn)
VALUES
( #{menuId}, #{menuNm}, #{menuPw}, CURRENT_TIMESTAMP, #{frstRegId}, CURRENT_TIMESTAMP, #{lastProcId}
, #{useAt}  
 )

mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT menu_id, menu_nm, menu_pw, frst_reg_dt, frst_reg_id, last_proc_dt, last_proc_id , menu_yn , row_number() over () AS rownum FROM tb_cm_menu a WHERE 1=1
AND a.USE_AT = #{useAt}
AND a.menu_nm LIKE '%' || #{searchWrd} || '%'
AND a.menu_id LIKE '%' || #{searchWrd} || '%'
ORDER BY a.frst_reg_dt DESC ) TB )
WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT 
	count(menu_id)
FROM
	tb_cm_menu a
WHERE a.menu_id = #{menuId}

	AND a.menu_pw = #{menuPw}
