mapper.insert:

INSERT INTO tb_cm_user
(user_id, user_nm, user_pw, frst_reg_dt, frst_reg_id, last_proc_dt, last_proc_id
, use_at)
VALUES
( #{userId}, #{userNm}, #{userPw}, CURRENT_TIMESTAMP, #{frstRegId}, CURRENT_TIMESTAMP, #{lastProcId}
, #{useAt}  
 )

mapper.select:
SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT user_id, user_nm, user_pw, frst_reg_dt, frst_reg_id, last_proc_dt, last_proc_id , use_at , row_number() over () AS rownum FROM tb_cm_user a WHERE 1=1
AND a.USE_AT = #{useAt}
AND a.user_nm LIKE '%' || #{searchWrd} || '%'
AND a.user_id LIKE '%' || #{searchWrd} || '%'
ORDER BY a.frst_reg_dt DESC ) TB )
WHERE rn BETWEEN #{firstIndex} + 1 AND #{firstIndex} + #{recordCountPerPage}

SELECT 
	count(user_id)
FROM
	tb_cm_user a
WHERE a.user_id = #{userId}

	AND a.user_pw = #{userPw}
